import { useEffect } from "react"
import { useState } from "react/cjs/react.development"
import useSound from "use-sound"
import play from './assets/play.wav'



export default function QuestionsBox({  setquestionnumber,setstop,data,questionnumber }) {
    console.log(data)
  const [question,setquestion]=useState(null)
  const [selectedanswer,setselectedanswer]=useState([])
  const [classname,setclassname]=useState('answer')
  const [letsPlay]=useSound(play)

  useEffect(()=>{
      letsPlay()
      
  },[letsPlay])
 
  const Checkit=(a)=>{
         if (a.correct){
            setquestionnumber(questionnumber+1)
             setselectedanswer(null)
            
        }
        else{
            setquestionnumber(questionnumber)
            setselectedanswer(null)
            setstop(true)
        }
  }
  useEffect(()=>{
      setquestion(data[questionnumber-1])
  },[data,questionnumber])
  const Handleclick=(a)=>{
     setselectedanswer(a)
     setclassname('answer active')
     setTimeout(() => {
         setclassname(a.correct?'answer correct':'answer wrong')
         
     }, 3000);
  setTimeout(() => {
      Checkit(a)
    
      
  }, 6000);
    
  }

    return (
        <div className='qbox'>
            <div className="question">{question?.question}</div>
            <div className='answers'>
                {question?.answers.map(b=>(
                     <div    className={selectedanswer===b?classname:'answer'}  onClick={()=>Handleclick(b)}>{b.text}</div>

                ))}
            
            
            </div>
           
            
        </div>
    )
}
