        const interval= setInterval(() => {
            settimer((prev)=>prev-1)
            
        }, 1000);
        return ()=>clearInterval(interval)
    },[setstop,timer])

    useEffect(()=>{
        settimer(30)
    },[questionnumber])
    return (

      timer
    )
}
